mapper.select:
/*根据用户id获取一级菜单*/
select tm.*,tpo.* from tb_module tm,tb_permit_role tpr,tb_role tr,tb_personinfo tpo,tb_user tu
where tm.m_level=0 and tpr.m_id=tm.m_id and tr.r_id=tpr.r_id and tr.r_id=#{userid} and tpo.persoId=tu.persoId and tu.u_id=tr.r_id
group by tm.m_id asc;

/*根据一级菜单获取二级菜单*/
select tm.* from tb_module tm,tb_permit_role tpo,tb_role tr,tb_role_user tru,tb_user tu
where tm.m_level=#{m_id} and tu.u_id=#{userid} and tm.m_id=tpo.m_id and tpo.r_id=tr.r_id and tr.r_id=tru.r_id and tru.u_id=tu.u_id
group by tm.m_id asc ;
